import React from 'react';
import './header.css'
import {useContext} from 'react';
import UserContext from '../../UserContext'

function Header() {
    const {user, setUser} = useContext(UserContext);
    const signout = () =>{
        localStorage.removeItem("user");
        setUser(null);
    }
    return (
        <div className="navbar">
            <img className="hamburger" src="data:image/svg+xml;base64," alt=""/>
            <img className="logo1" src="https://stackoverflow.design/assets/img/logos/so/logo-stackoverflow.svg" alt="" />
            <span>
                About
            </span>
            <span>
                Products
            </span>
            <span>
                ForTeams
            </span>
            <input type="text" name="" id="" className="searchbar" placeholder="Search..."/>
            {
                !user ? (<span><button className="button1">Log in</button>
            <button className="button2">Sign up</button></span>) : <button className="button1" onClick={signout}>Sign out</button>
            }
            
        </div>
    );
}

export default Header;